mapper.select:
SELECT id
FROM users
WHERE id=#{inputId}

SELECT pwd
FROM users
WHERE id=#{inputId}

SELECT id, pwd, email, regdate, profile
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET profile=#{profile}
WHERE id=#{id}

UPDATE users
SET pwd=#{pwd}
WHERE id=#{id}

UPDATE users
SET email=#{email}
WHERE id=#{id}
